  <header>

  </header>

  <body>
    <div class="wrapper">
      <div id="content">
        {location.state.name}
        <Table props = {importedStudents}/>

      </div>
      <footer class="footer">

        <input
                    class ="btn btn-primary"
                    type="file"
                    name="file"
                    accept=".csv"
                    onChange={changeHandler}
                  />



          <button
            class="btn btn-primary"
            style={{ backgroundColor: "RGB(100, 239, 100", borderColor: "RGB(100, 239, 100)" }}
            onClick={(e) => {
              sendFile();




