import react from "react";
import { useNavigate } from "react-router-dom"; // import the useNavigate function from react-router-dom that will help us route to other pages and pass data as we do it
import './signin.css'
import PropTypes from 'prop-types';
import { useState } from "react";

async function loginUser(credentials) {
  return fetch('http://localhost:3001/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(credentials)
  })
    .then(data => data.json())
 }


function SignIn({ setToken }) {

  function  toApp () {
    navigate("/app");
  }
  const navigate = useNavigate(); // intiales an instance of the useNavigate function and use this instance to route
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();

  const handleSubmit = async e => {
    e.preventDefault();
    const token = await loginUser({
      username,
      password
    });
    setToken(token);
    navigate("/app");
  }




  return(
    <div>
      <body>
        <main>
          <div class="row">
            <div class="colm-form">
              <div class="form-container">
              <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Email address or phone number" onChange={e => setUserName(e.target.value)}/>
                  <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} />
                    <button class="btn-login" type = "submit">{/*when the user click on the login button it will call the toApp function*/} 
                      Login
                    </button>
                </form>
                    <a href="#">Forgotten password?</a>
                  </div>
              </div>
            </div>
        </main>

      </body>
    </div>
  )}


  SignIn.propTypes = {
    setToken: PropTypes.func.isRequired
  }; 

export default SignIn;
