import "bootstrap/dist/css/bootstrap.min.css";
import { useNavigate } from "react-router-dom";

import "./componentsCSS.css";

function Table(props) {
  const navigate = useNavigate(); // look at courses.js to find out what we are doing here

  const toStudent = (i) => {
    navigate("student", { state: props.props[i] }); // navigate to the student page and send the student data (props.props[i] [one specific student object]) to the student page
  };
  /*
  function header(index) {
    switch (index) {
      case 1:
        return (
          <tr class="table">
            <th scope="col">#</th> 
            <th scope="col">Name</th>
            <th scope="col">ID</th>
            <th scope="col">lab assignment</th>
            <th scope="col">knowledge checks</th>
            <th scope="col">tests</th>
            <th scope="col">Participation</th>
            <th scope="col">Final Grade</th>
          </tr>
        );
        break;
      case 2:
        return (
        <tr class="table">
        <th scope="col">#</th> 
        <th scope="col">Student ID</th>
        <th scope="col">Student Name</th>
        <th scope="col">Course % Grade</th>
        <th scope="col">Course Lettr Grade</th>

      </tr>
    );
    break;
    }
  }

  function body(index, content) {
    switch (index) {
      case 1:
        return (
          <tbody class="table">
            {content.map((e) => (
              <tr
                onClick={() => {
                  toStudent(parseInt(e.no - 1));
                }}
              >
                {" "}

                <th scope="row">{e.no}</th>{" "}
                <td>{e.name}</td>
                <td>{e.id}</td>
                <td>{e.lab}</td>
                <td>{e.knowledge}</td>
                <td>{e.tests}</td>
                <td>{e.participation}</td>
                <td>{e.final}</td>
              </tr>
            ))}
          </tbody>
        );
    }
  } */

  return (
    <table class="table">
      {" "}
      {/*this is the table that will display the students data*/}
      <thead>
        {" "}
        <tr class="table">
        {props.props.map((e) => (
          <th scope="col">{e}</th>
        ))}
        </tr>
      </thead>
      <tbody class="table">
        {props.props.map((e) => (
          <tr
            onClick={() => {
              toStudent(parseInt(e.no - 1));
            }}
          >
            {" "}
            <th scope="row">{e.no}</th> <td>{e.name}</td>
            <td>{e.id}</td>
            <td>{e.lab}</td>
            <td>{e.knowledge}</td>
            <td>{e.tests}</td>
            <td>{e.participation}</td>
            <td>{e.final}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default Table; // export the component so we can use it in other files
