      <RequireAuth loginPath="/login">
        <MainPage />
      </RequireAuth>
    ),
  },
  {
    path: "Students",
    element: <StudentDetail />,
  },
  {
    path: "/login",
    element: <SignIn />,
  },
  {
    //make the path url dynamic
    path: `/CoursePage/course`,
    element: <CoursePage />,
  },

  {
    path: `/CoursePage/course/CourseDetails`,
    element: <CourseDetails />,
  },
  {
    path: "/course/student",
    element: <StudentSummaryPage />,
  },
  {
    path: "/admin",
    element: <Adm />,
  },
  {
    path: "*",
    element: <NotFound />,
  },
]); */

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
  <AuthProvider authType={"cookie"} authName={"_auth"} cookieDomain={window.location.hostname} cookieSecure={false}>
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<MainPage />} />
        <Route path="/course" element={<App />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
    </BrowserRouter>
    </AuthProvider>

  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
