import React from "react";
import ReactDOM from "react-dom/client";
import App from "./App";
import reportWebVitals from "./reportWebVitals";
import SignIn from "./pages/signIn";
import CoursePage from "./pages/CoursePage";
import StudentSummaryPage from "./pages/StudentSummaryPage";
import NotFound from "./pages/PageNotFound";
import { createBrowserRouter, RouterProvider, Route, BrowserRouter } from "react-router-dom";
import Adm from "./pages/Admin";
import MainPage from "./pages/MainPage";
import StudentDetail from "./pages/StudentDetail";
import CourseDetails from "./pages/courseDetails";
import { AuthProvider } from 'react-auth-kit'
import { RequireAuth } from "react-auth-kit";
import { Routes } from "react-router-dom";


const router = createBrowserRouter([
  {
    path: "coursePage",
    element: <App />,
  },
  {
    path: "/",
    element: <RequireAuth loginPath="/login">
    <MainPage />
  </RequireAuth>,

  },
  {
    path: "Students",
    element: <StudentDetail />,

  },
  {
    path: "/login",
    element: <SignIn />,
  },
  {
    //make the path url dynamic
    path:`/CoursePage/course`,
    element: <CoursePage />,
  },

  {
    path:`/CoursePage/course/CourseDetails`,
    element: <CourseDetails />,

  },
  {
    path: "/course/student",
    element: <StudentSummaryPage />,
  },
  {
    path: "/admin",
    element: <Adm />,},
  {
    path: "*",
    element: <NotFound />,
  }
]);

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<AuthProvider
          authType={"cookie"}
          authName={"_auth"}
          cookieDomain={window.location.hostname}
          cookieSecure={false}
        ><BrowserRouter><Routes>
          <Route path="/" element={<MainPage/>} />
          </Routes></BrowserRouter>   </AuthProvider>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
